mapper.select:
        SELECT sales_order.sale_id,seller_id,manager_id,customer_id,remarks,time,status,delivery_status,product_id,unit_price,num
FROM xietong.sales_order,sales_product
where sales_order.sale_id=sales_product.sale_id and sales_order.effective=1 and sales_product.effective=1 and sales_order.sale_id=#{saleId}

        SELECT sales_order.sale_id,shipment.shipment_id,shipment.time,shipment.status,shipment_product.product_id,num
FROM xietong.shipment,sales_order,shipment_product
where shipment.sale_id=sales_order.sale_id and shipment.shipment_id=shipment_product.shipment_id and shipment.effective=1 and shipment_product.effective=1 and sales_order.sale_id=#{saleId}
